        var oOpa = new Opa();

        // this statement will time out after 15 seconds and poll every 400ms
        // those two values come from the defaults of sap.ui.test.Opa.config
        oOpa.waitFor({
        });

        // All wait for statements added after this will take other defaults
        Opa.extendConfig({
            timeout: 10,
