Templates.template:
fireDownloadCompleted(
	{
		viewableLoadInfos: [${viewableLoadInfos}], // sap.ui.vtm.ViewableLoadInfo[]
		downloadedViewables: [${downloadedViewables}] // sap.ui.vtm.Viewable[]
	}
)

fireDownloadProgress(
	{
		viewable: ${viewable}, // sap.ui.vtm.Viewable
		downloadedBytes: ${downloadedBytes}, // int
		totalBytes: ${totalBytes} // int
	}
)

fireDownloadStarted(
	{
		viewableLoadInfos: [${viewableLoadInfos}] // sap.ui.vtm.ViewableLoadInfo[]
	}
)

fireLoadCompleted(
	{
		succeeeded: ${bSucceeeded}, // boolean
		viewableLoadInfos: [${viewableLoadInfos}], // sap.ui.vtm.ViewableLoadInfo[]
		loadedViewables: [${loadedViewables}] // sap.ui.vtm.Viewable[]
	}
)

fireLoadStarted(
	{
		viewableLoadInfos: [${viewableLoadInfos}] // sap.ui.vtm.ViewableLoadInfo[]
	}
)

fireDragOver(
	{
		dragItem: ${dragItem}, // object
		dragTree: ${dragTree}, // sap.ui.vtm.Tree
		dragOverItem: ${dragOverItem}, // object
		dragOverTree: ${dragOverTree} // sap.ui.vtm.Tree
	}
)

fireDragStart(
	{
		dragItem: ${dragItem}, // object
		dragTree: ${dragTree} // sap.ui.vtm.Tree
	}
)

fireDrop(
	{
		dragItem: ${dragItem}, // object
		dragTree: ${dragTree}, // sap.ui.vtm.Tree
		dropItem: ${dropItem}, // object
		dropTree: ${dropTree} // sap.ui.vtm.Tree
	}
)

fireExpandedChanged(
	{
		item: ${item}, // object
		expanded: ${bExpanded}, // boolean
		userInteraction: ${bUserInteraction} // boolean
	}
)

fireMessageStatusIconClicked(
	{
		items: ${items}, // object
		control: ${control} // sap.ui.core.Control
	}
)

fireSelectionChanged(
	{
		addedItems: [${addedItems}], // object[]
		removedItems: [${removedItems}], // object[]
		userInteraction: ${bUserInteraction} // boolean
	}
)

fireVisibilityHeaderIconClicked(
	{
		visibility: ${bVisibility}, // boolean
		control: ${control} // sap.ui.core.Control
	}
)

fireVisibilityIconClicked(
	{
		item: ${item}, // object
		visibility: ${bVisibility}, // boolean
		control: ${control} // sap.ui.core.Control
	}
)

fireHover(
	{
		x: ${x}, // float
		y: ${y}, // float
		nodeId: "${nodeId}" // string
	}
)

fireSelectionChanged(
	{
		selectedIds: [${selectedIds}], // string[]
		unselectedIds: [${unselectedIds}], // string[]
		userInteraction: ${bUserInteraction} // boolean
	}
)

fireViewChanged(
	{
		cameraInfo: ${cameraInfo} // object
	}
)

fireVisibilityChanged(
	{
		visibleIds: [${visibleIds}], // string[]
		hiddenIds: [${hiddenIds}], // string[]
		userInteraction: ${bUserInteraction} // boolean
	}
)

firePanelAdded(
	{
		panel: ${panel} // sap.ui.vtm.Panel
	}
)
